CREATE TABLE voivodeships (  id INTEGER PRIMARY KEY,  name TEXT);CREATE TABLE counties (  id INTEGER PRIMARY KEY,  name TEXT,  voivodeship_id INTEGER,  FOREIGN KEY (voivodeship_id) REFERENCES voivodeships(id));CREATE TABLE community_types (  id INTEGER PRIMARY KEY,  type TEXT);CREATE TABLE communities (  id INTEGER PRIMARY KEY,  name TEXT,  county_id INTEGER,  community_type_id INTEGER,  FOREIGN KEY (county_id) REFERENCES counties(id),  FOREIGN KEY (community_type_id) REFERENCES community_types(id));